  TYPE VARCHAR(20) NOT NULL ,
  STORAGE_NAME VARCHAR(255) NOT NULL ,
  STORAGE_URL VARCHAR(255) NOT NULL ,
  CREATE_ID BIGINT(20) DEFAULT NULL ,
  CREATE_NAME VARCHAR(100) DEFAULT NULL ,
  CREATE_DATE DATETIME DEFAULT NULL ,
  UPDATE_ID BIGINT(20) DEFAULT NULL ,
  UPDATE_NAME VARCHAR(100) DEFAULT NULL ,
  UPDATE_DATE DATETIME DEFAULT NULL ,
  DEL_FLAG TINYINT(4) NOT NULL DEFAULT '0' ,
  PRIMARY KEY (ID)
);

DROP TABLE IF EXISTS SYS_ATTACHMENT_DIR;
CREATE TABLE SYS_ATTACHMENT_DIR (
  ID BIGINT(20) NOT NULL ,
  PARENT_ID BIGINT(20) NOT NULL ,
  PARENT_IDS VARCHAR(2000) NOT NULL ,
  NAME VARCHAR(100) NOT NULL ,
  CODE VARCHAR(20) DEFAULT NULL ,
  CREATE_ID BIGINT(20) DEFAULT NULL ,
  CREATE_NAME VARCHAR(100) DEFAULT NULL ,
  CREATE_DATE DATETIME DEFAULT NULL ,
  UPDATE_ID BIGINT(20) DEFAULT NULL ,
  UPDATE_NAME VARCHAR(100) DEFAULT NULL ,
  UPDATE_DATE DATETIME DEFAULT NULL ,
  DEL_FLAG TINYINT(4) NOT NULL DEFAULT '0' ,
  LEVEL TINYINT(4) DEFAULT NULL,
  PATH VARCHAR(2000) DEFAULT NULL,
  PRIMARY KEY (ID)
);

DROP TABLE IF EXISTS SYS_DICT;
CREATE TABLE SYS_DICT (
  ID BIGINT(20) NOT NULL ,
  NAME VARCHAR(200) NOT NULL ,
  CODE VARCHAR(200) NOT NULL ,
  VALUE VARCHAR(2000) NOT NULL ,
  SORT INT(11) DEFAULT '0' ,
  CREATE_ID BIGINT(20) DEFAULT NULL ,
  CREATE_NAME VARCHAR(100) DEFAULT NULL ,
  CREATE_DATE DATETIME DEFAULT NULL ,
  PRIMARY KEY (ID)
);

DROP TABLE IF EXISTS SYS_DUAL;
CREATE TABLE SYS_DUAL (
  X CHAR(1) NOT NULL DEFAULT 'X' ,
  PRIMARY KEY (X)
);

DROP TABLE IF EXISTS SYS_EMAIL_TASK;
CREATE TABLE SYS_EMAIL_TASK (
  ID BIGINT(20) NOT NULL ,
  EMAIL_FORM VARCHAR(64) DEFAULT NULL ,
  EMAIL_TITLE VARCHAR(255) DEFAULT NULL ,
  EMAIL_TO VARCHAR(1000) DEFAULT NULL ,
  EMAIL_CC VARCHAR(1000) DEFAULT NULL ,
  EMAIL_TEMPLATE VARCHAR(100) DEFAULT NULL ,
  EMAIL_CONTENT TEXT ,
  CREATE_ID BIGINT(20) DEFAULT NULL ,
  CREATE_NAME VARCHAR(100) DEFAULT NULL ,
  CREATE_DATE DATETIME DEFAULT NULL ,
  SEND_FLAG TINYINT(4) DEFAULT NULL ,
  STATUS TINYINT(4) DEFAULT NULL ,
  PRIMARY KEY (ID)
);

DROP TABLE IF EXISTS SYS_EMAIL_TASK_OLD;
CREATE TABLE SYS_EMAIL_TASK_OLD (
  ID BIGINT(20) NOT NULL ,
  EMAIL_FORM VARCHAR(64) DEFAULT NULL ,
  EMAIL_TITLE VARCHAR(255) DEFAULT NULL ,
  EMAIL_TO VARCHAR(1000) DEFAULT NULL ,
