  ADDRESS VARCHAR(100) DEFAULT NULL ,
  PHONE VARCHAR(100) DEFAULT NULL ,
  ZIP_CODE VARCHAR(100) DEFAULT NULL ,
  EMAIL VARCHAR(100) DEFAULT NULL ,
  CERTTEXT VARCHAR(100) DEFAULT NULL ,
  IS_SITE_ENABLED TINYINT(4) DEFAULT '1' ,
  SITE_CLOSE_MESSAGE VARCHAR(500) DEFAULT NULL ,
  LOCALE VARCHAR(10) DEFAULT 'ZH_CN' ,
  WATERMARK_ALPHA VARCHAR(10) DEFAULT '50' ,
  WATERMARK_IMAGE_FILE VARCHAR(255) DEFAULT NULL ,
  WATERMARK_POSITION VARCHAR(2) DEFAULT 'RB' ,
