  SAFE_KEY_EXPIRY_TIME INT(11) DEFAULT '30' ,
  UPLOAD_MAX_SIZE INT(11) DEFAULT '10' ,
  UPLOAD_IMAGE_MAX_SIZE INT(11) DEFAULT '10' ,
  UPLOAD_MEDIA_MAX_SIZE INT(11) DEFAULT '10' ,
  UPLOAD_FILE_MAX_SIZE INT(11) DEFAULT '10' ,
  UPLOAD_IMAGE_EXTENSION VARCHAR(255) DEFAULT 'JPG,JPEG,BMP,GIF,PNG' ,
  UPLOAD_MEDIA_EXTENSION VARCHAR(255) DEFAULT 'SWF,FLV,MP3,WAV,AVI,RM,RMVB' ,
  UPLOAD_FILE_EXTENSION VARCHAR(255) DEFAULT 'ZIP,RAR,7Z,DOC,DOCX,XLS,XLSX,PPT,PPTX' ,
  IMAGE_UPLOAD_PATH VARCHAR(255) DEFAULT NULL ,
  MEDIA_UPLOAD_PATH VARCHAR(255) DEFAULT NULL ,
  FILE_UPLOAD_PATH VARCHAR(255) DEFAULT NULL ,
  SMTP_HOST VARCHAR(20) DEFAULT NULL ,
  SMTP_PORT VARCHAR(10) DEFAULT NULL ,
  SMTP_USERNAME VARCHAR(100) DEFAULT NULL ,
  SMTP_PASSWORD VARCHAR(100) DEFAULT NULL ,
  SMTP_SSL_ENABLED TINYINT(4) DEFAULT '0' ,
  SMTP_ANONYMOUS_ENABLED TINYINT(4) DEFAULT '0' ,
  SMTP_TIMEOUT VARCHAR(100) DEFAULT NULL ,
  IS_MULTI_LOGIN TINYINT(4) DEFAULT '1' ,
  PRIMARY KEY (ID)
);

DROP TABLE IF EXISTS SYS_TASK;
CREATE TABLE SYS_TASK (
  ID BIGINT(20) NOT NULL ,
  NAME VARCHAR(64) NOT NULL ,
  CRON_EXPRESSION VARCHAR(100) DEFAULT NULL ,
  ALLOW_EXECUTE_COUNT INT(11) DEFAULT NULL ,
