  REMARKS VARCHAR(255) DEFAULT NULL ,
  DEL_FLAG TINYINT(4) NOT NULL DEFAULT '0' ,
  CONCURRENT TINYINT(4) NOT NULL DEFAULT '0' ,
  PARAMS VARCHAR(255) DEFAULT NULL ,
  OPS VARCHAR(255) DEFAULT NULL ,
  PRIMARY KEY (ID)
);

